mapper.select:
select

from base_detect_method
where delete_flag=0 and id = #{id,jdbcType=VARCHAR}

select

from base_detect_method
where delete_flag=0 and isCheck=0 and detect_modular_id = #{detectModularId,jdbcType=VARCHAR}

mapper.delete:
UPDATE base_detect_method
SET delete_flag = 1, update_date = now()
WHERE id = #{id,jdbcType=VARCHAR}

mapper.sql:
where 1=1 and delete_flag=0
and detect_method like CONCAT('%', #{obj.detectMethod}, '%')
and detect_modular_id = #{obj.detectModularId}

mapper.select:
select

from base_detect_method

order by create_date ${order} limit #{rowOffset}, #{pageSize}

SELECT id
FROM base_detect_method
WHERE delete_flag = 0
    AND detect_method = #{detectMethod}
    AND detect_modular_id = #{modularId}
LIMIT 0,1

mapper.update:
UPDATE base_detect_method
SET
    delete_flag = 1,
    update_by   = #{updateBy,jdbcType=VARCHAR},
    update_date = #{updateDate,jdbcType=TIMESTAMP}
WHERE detect_modular_id = #{detectModularId,jdbcType=VARCHAR}
