mapper.delete:
UPDATE base_detect_modular
SET delete_flag = 1, update_date = now()
WHERE id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from base_detect_modular

order by create_date ${order} limit #{rowOffset}, #{pageSize}

SELECT id FROM base_detect_modular WHERE detect_modular = #{detectModular} AND delete_flag = 0
AND id != #{id}
LIMIT 0, 1
