mapper.select:
SELECT r.device_id,r.depart_id,r.delete_flag, r.start_date,r.end_date FROM base_device_point_rel r
WHERE r.device_id=#{deviceId}
ORDER BY r.create_date DESC

UPDATE base_device_point_rel rel SET delete_flag=1
WHERE rel.delete_flag=0 AND rel.depart_id in (
	SELECT id FROM t_s_depart t1 WHERE t1.depart_code LIKE CONCAT((
		SELECT depart_code FROM t_s_depart t2 WHERE id=#{departId}
	),'%')
)

SELECT * FROM base_device_point_rel rel 
WHERE rel.delete_flag=0 AND rel.depart_id in (
	SELECT id FROM t_s_depart t1 WHERE t1.depart_code LIKE CONCAT((
		SELECT depart_code FROM t_s_depart t2 WHERE id=#{departId}
	),'%')
)
