mapper.sql:
where delete_flag = 0



		and law_name like CONCAT('%', #{obj.baseBean.lawName}, '%')


		and law_type = #{obj.baseBean.lawType}


		and law_status = #{obj.baseBean.lawStatus}


		and use_status = #{obj.baseBean.useStatus}



	and use_status=1

mapper.delete:
     update base_laws_regulations set delete_flag=1,update_date=now()
	where id in

		#{ids}

mapper.select:
select 
from base_laws_regulations 

order by update_date ${order} limit #{rowOffset}, #{pageSize}

select 

from base_laws_regulations
where delete_flag = 0 and law_name = #{lawName}
