    #{id}

mapper.select:
SELECT durl.*, ( CASE durl.create_by WHEN 0 THEN '定时系统' ELSE u.user_name END ) AS user_name FROM data_unqualified_recording_log durl LEFT JOIN t_s_user u ON durl.create_by = u.id WHERE durl.delete_flag = 0 AND durl.dur_id = #{obj.durId}
HAVING durl.content LIKE CONCAT('%',#{obj.keyWords},'%') OR user_name LIKE CONCAT('%',#{obj.keyWords},'%')
ORDER BY durl.create_date DESC LIMIT #{rowOffset}, #{pageSize}

SELECT count(1) AS rowTotal FROM ( SELECT durl.*, ( CASE durl.create_by WHEN 0 THEN '定时系统' ELSE u.user_name END ) AS user_name FROM data_unqualified_recording_log durl LEFT JOIN t_s_user u ON durl.create_by = u.id WHERE durl.delete_flag = 0 AND durl.dur_id = #{obj.durId}
HAVING durl.content LIKE CONCAT('%',#{obj.keyWords},'%') OR user_name LIKE CONCAT('%',#{obj.keyWords},'%')
)tb1
