mapper.select:
select 

from detect_car_location
where car_imei=#{imei} and update_date=#{updateDate}

    select 

    from detect_car_location
    where car_imei=#{carImei}

    	and speed=0

	and update_date >= #{startTime}
	and update_date <=#{endTime}
 	order by update_date

    SELECT DATE_FORMAT(update_date,'%Y-%m-%d') id,DATE_FORMAT(update_date,'%Y-%m-%d') showDate from detect_car_location 
	where car_imei=#{carImei}
	GROUP BY showDate
	ORDER BY showDate desc LIMIT 0,10;
