mapper.select:
    select 
     	distinct	    

    from base_ledger_obj_history
  	where 1=1
			and delete_flag=0

  			and user_id=#{userId}


  			and type=#{type}


				and user_type=#{userType}


				and keyword=#{keyword}


				and key_type=#{keyType}


				and regname=#{regname}

 	 	order by history_count  desc  limit 0,10

    select 
    DISTINCT 
    from base_ledger_obj_history
  	where 1=1
			and delete_flag=0

  			and user_id=#{userId}


  			and type=#{type}


				and user_type=#{userType}


				and key_type=#{keyType}


				and regname=#{regname}

			GROUP BY regname 
 	 	order by history_count  desc  limit 0,10

    select 
    DISTINCT 
    from base_ledger_obj_history
  	where 1=1
			and delete_flag=0

  			and user_id=#{userId}


  			and type=#{type}


				and user_type=#{userType}


				and key_type=#{keyType}


				and regname=#{regname}

			GROUP BY keyword 
 	 	order by history_count  desc  limit 0,10
