mapper.select:
    select 
    u.* ,o.management_type as management_type
    from base_ledger_user   u
     LEFT JOIN base_regulatory_object o on u.reg_id=o.id 
    LEFT JOIN base_regulatory_business busi on u.ope_id=busi.id
    where u.delete_flag=0 and o.delete_flag=0 

		and	username=#{username}


		and	pwd=#{pwd} 


		and	type=#{type}
