  values (#{id,jdbcType=VARCHAR}, #{regType,jdbcType=VARCHAR}, #{regTypeCode,jdbcType=VARCHAR},
    #{showBusiness,jdbcType=SMALLINT}, #{sorting,jdbcType=SMALLINT}, #{checked,jdbcType=SMALLINT},
