mapper.sql:
where delete_flag = 0



		and reg_type like concat(concat('%',#{obj.regulatoryType.regType}),'%')


		and checked = #{obj.regulatoryType.checked}

mapper.select:
select 
from base_regulatory_type

order by sorting asc,reg_type asc limit #{rowOffset}, #{pageSize}
