mapper.sql:
where delete_flag = 0



            and function_id= #{obj.baseBean.functionId}


            and operation_name like CONCAT('%', #{obj.baseBean.operationName}, '%')

mapper.delete:
UPDATE t_s_operation SET delete_flag=1,update_date=now()
WHERE id IN

    #{ids}

mapper.update:
UPDATE t_s_operation
SET delete_flag = 1, update_date = now()
WHERE function_id = #{id}

UPDATE t_s_operation
SET delete_flag = 0, update_date = now()
WHERE function_id = #{id}

mapper.select:
SELECT

FROM t_s_operation

ORDER BY sorting ${order} LIMIT #{rowOffset}, #{pageSize}

SELECT

FROM t_s_operation
WHERE function_id = #{functionId,jdbcType=VARCHAR}
AND operation_name=#{operationName,jdbcType=VARCHAR}
AND delete_flag=0
