mapper.delete:
delete from t_s_role_function
where role_id in

	#{ids}

delete from t_s_role_function
where function_id in

	#{functionId}


	and role_id=#{roleId}

delete from t_s_role_function
where 1=1

	and function_id =#{functionId}
	or function_id in (select id from t_s_operation where function_id=#{functionId} )


	and function_id in

		#{id}
