from inspection_report_price a
where id = #{id,jdbcType=INTEGER}

mapper.delete:
update inspection_report_price set delete_flag=1,update_date=now()
where id in

       #{ids}

mapper.select:
select
from inspection_report_price a LEFT JOIN inspection_unit b on b.id = a.inspection_unit_id
LIMIT #{rowOffset}, #{pageSize}

mapper.sql:
where a.delete_flag=0

and  a.checked = #{obj.checked}


AND b.company_name like '%${obj.companyName}%'
