mapper.select:
    select config.*,ctype.config_code from system_config config
	left join system_config_type ctype on config.config_type_id=ctype.id
    where config.id = #{id,jdbcType=INTEGER}

mapper.delete:
update system_config set delete_flag=1,update_date=now()
   where id in

       #{ids}

mapper.select:
select config.*,ctype.config_code,ctype.config_type_name from system_config config
left join system_config_type ctype on config.config_type_id=ctype.id
where config.delete_flag=0 and ctype.delete_flag=0 
and project_id=#{obj.projectID}

	and ctype.config_type_name like CONCAT('%',#{obj.keyWords},'%')

order by config.update_date ${order} limit #{rowOffset}, #{pageSize}
